import { Factura } from "./factura";

/**
 * Clase que representa un gestor de facturación.
 */
export class GestorFacturacion {
 private facturas: Factura[] = [];

 /**
  * Constructor de la clase GestorFacturacion.
  * @param facturas - Un array opcional de facturas para inicializar el gestor.
  */
 constructor(facturas?: Factura[]) {
  if (facturas) this.facturas = facturas;
 }

 /**
  * Devuelve las facturas del gestor.
  * @returns Un array de facturas.
  */
 getFacturas(): Factura[] {
  return this.facturas;
 }

 /**
  * Añade una factura al gestor.
  * @param factura - La factura a añadir.
  */
 addFactura(factura: Factura): void {
  this.facturas.push(factura);
 }

 /**
  * Elimina una factura del gestor.
  * @param factura - La factura a eliminar.
  */
 removeFactura(factura: Factura): void {
  const index = this.facturas.indexOf(factura);
  if (index !== -1) this.facturas.splice(index, 1);
 }

 /**
  * Calcula el total facturado por el gestor.
  * @returns El total facturado.
  */
 totalFacturado(): number {
  return this.facturas.reduce(
   (total, factura) => total + factura.getImporte(),
   0
  );
 }
}

import { FileService } from "./file_service";

/**
 * Clase que representa un gestor de archivos.
 */
export class FileManager {
 /**
  * Constructor de la clase FileManager.
  * @param fileOperation - Una instancia de FileService para realizar operaciones de archivo.
  */
 constructor(private readonly fileOperation: FileService) {}

 /**
  * Lee un archivo del sistema de archivos.
  * @param filePath - La ruta del archivo a leer.
  * @returns El contenido del archivo como una cadena de texto.
  */
 public readFile(filePath: string): string {
  return this.fileOperation.read(filePath);
 }

 /**
  * Escribe datos en un archivo en el sistema de archivos.
  * @param filePath - La ruta del archivo donde se escribirán los datos.
  * @param data - Los datos a escribir en el archivo.
  */
 public writeFile(filePath: string, data: string): void {
  this.fileOperation.write(filePath, data);
 }
}

import * as fs from "fs";
import { FileService } from "./file_service";

/**
 * Clase que proporciona operaciones de archivos utilizando el módulo 'fs' de Node.js.
 * Implementa la interfaz FileService.
 */
export class FileSystemProvider implements FileService {
 /**
  * Constructor de la clase FileSystemProvider.
  * @param fsProvider - Una instancia del módulo 'fs' de Node.js.
  */
 constructor(private readonly fsProvider: typeof fs) {}

 /**
  * Lee un archivo del sistema de archivos.
  * @param filePath - La ruta del archivo a leer.
  * @returns El contenido del archivo como una cadena de texto.
  * @throws {Error} Si ocurre un error durante la lectura del archivo.
  */
 read(filePath: string): string {
  try {
   if (filePath === "") throw TypeError;
   return this.fsProvider.readFileSync(require.resolve(filePath), "utf-8");
  } catch (error) {
   let message = "";
   if (error instanceof Error) message = error.message;
   throw new Error(`Error en la lectura del fichero: ${message}`);
  }
 }

 /**
  * Escribe datos en un archivo en el sistema de archivos.
  * @param filePath - La ruta del archivo donde se escribirán los datos.
  * @param data - Los datos a escribir en el archivo.
  * @throws {Error} Si ocurre un error durante la escritura del archivo.
  */
 write(filePath: string, data: string): void {
  try {
   if (filePath === "") throw TypeError;
   this.fsProvider.writeFileSync(require.resolve(filePath), data, "utf-8");
   console.log("Archivo escrito exitosamente.");
  } catch (error) {
   let message = "";
   if (error instanceof Error) message = error.message;
   throw new Error(`Error en la edición del fichero: ${message}`);
  }
 }
}

/**
 * Interfaz que representa un objeto imprimible.
 */
interface Printable {
 /**
  * Método para imprimir.
  */
 print(): void;
}

/**
 * Interfaz que representa un objeto escaneable.
 */
interface Scannable {
 /**
  * Método para escanear.
  */
 scan(): void;
}

/**
 * Clase que representa una impresora.
 * Implementa la interfaz Printable.
 */
export class Printer implements Printable {
 /**
  * Método para imprimir.
  */
 print(): void {
  console.log("Printing...");
 }
}

/**
 * Clase que representa un escáner.
 * Implementa la interfaz Scannable.
 */
export class Scanner implements Scannable {
 /**
  * Método para escanear.
  */
 scan(): void {
  console.log("Scanning...");
 }
}

/**
 * Clase que representa una impresora y escáner.
 * Implementa las interfaces Printable y Scannable.
 */
export class PrinterScanner implements Printable, Scannable {
 /**
  * Método para imprimir.
  */
 print(): void {
  console.log("Printing...");
 }

 /**
  * Método para escanear.
  */
 scan(): void {
  console.log("Scanning...");
 }
}

// Client code
const printer = new Printer();
// Printing
printer.print();

const scanner = new Scanner();
// Scanning
scanner.scan();

const printerScanner = new PrinterScanner();
// Printing
printerScanner.print();
// Scanning
printerScanner.scan();

